sys_ux_client_script.script:
/**
 * @param {params} params
 * @param {api} params.api
 * @param {any} params.event
 * @param {any} params.imports
 */
function handler({api, event, helpers, imports}) {

    // Fetch a GitHub zen quote from the Scripted REST API.
    helpers.snHttp('/api/x_1234_peer_review/github_zen/quote', {method: 'GET'})
    .then(({response}) => {
        // Fetch the API result and assign to the parameter.
        var quote = response.result;
        api.setState('ZenQuote',quote);
    })
    .catch(({error}) => {
        const message = `Error: ${error.data.error.message}`;
        console.error(message);
        api.emit('NOW_UXF_PAGE#ADD_NOTIFICATIONS', {
            id: 'alert5',
            status: 'high',
            icon: 'info-circle-outline',
            content: message,
            action: { type: 'dismiss' }
        });
    });

}
